//Problem 4 problemNumber++; wl(problemheader(problemNumber)); wl(&rsquo;&rsquo;); var s; var partName, problemName, buttonLabel, answerArray, correct; s = &ldquo;(a) &quot; + knoonlinecheck; wl(s); wl(kp); wl(kbr); s = &ldquo;(b.i) Give the threshhold for $\\ov{x}$ for which they should use issue a ticket.&rdquo;; wl(s); wl(kp); partName = problemNumber + &quot; (b.i)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 45.054, buttonLabel, 0.1, debugans); wl(kp); s = &ldquo;(b.ii) &quot; + knoonlinecheck; wl(s); wl(kp); wl(kbr); s = &ldquo;(c.i) What is the power of this test?&rdquo;; wl(s); wl(kp); partName = problemNumber + &quot; (c.i)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, .49257, buttonLabel, 0.001, debugans); wl(kp); s = &ldquo;(c.ii) How many cameras are needed to insure a power of 0.9?&rdquo;; wl(s); wl(kp); partName = problemNumber + &quot; (c.ii)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 10, buttonLabel, 0.001, debugans); wl(kp); whr(kdivcol,kdivwid);
