//Problem 1 problemNumber++; wl(problemheader(problemNumber)); var s; var partName, problemName, buttonLabel, answerArray, correct; s = [&lsquo;In these problems we want you to estimate probability intervals for various quantities. This is best described by example.&rsquo;, kp, boldtext(&lsquo;Example.&rsquo;) + &rsquo; Give your 50% probability interval for the median family income in Hungary. Do this by specifying the 25% lower bound and the 75% upper bound. Do not look anything up on the internet. Just make your best guess based on what you already know. There is no single correct answer.&rsquo;, kp, boldtext(&lsquo;Answer.&rsquo;) + &rsquo; The question asks me to give a lower bound $L$ and an upper bound $U$ so that I think/feel/believe that there is a 25% chance the true value is less than $L$ and a 25% chance the correct vaue is greater than $U$. This leaves a 50% probability the true value is between $L$ and $U$. &lsquo;, kp, &ldquo;Well let&rsquo;s see. I seem to recall the median income in the U.S. is about \\$50,000. I&rsquo;ll guess that Hungary is quite a bit lower, maybe around \\$32000. I&rsquo;m not all that confident it&rsquo;s not quite a bit lower than that. So my guess is that the 25% lower bound is \\$19,000 and the 75% bound is \\$40,000.&rdquo;]; wl(s.join(&rsquo; &lsquo;)); wl(kp); s = [&ldquo;Now it&rsquo;s you&rsquo;re turn. Without looking anything up online give your 25% lower bound and 75% upper bound for each of the following quantities.&rdquo;, kp, &ldquo;Answers we culled from the internet will be in the solution slides for this class.&rdquo;]; wl(s.join(&rsquo; &lsquo;)); wl(kp); s = &ldquo;(a) The total number of airline deaths in the last 100 years.&rdquo;; wl(s); wl(kp); s = &ldquo;25% lower bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (a.i)&quot;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;75% upper bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (a.ii)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;(b) The number of girls born in the world each year.&rdquo;; wl(s); wl(kp); s = &ldquo;25% lower bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (b.i)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;75% upper bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (b.ii)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;(c) The percentage (between 0 and 100) of African-Americans in the U.S. population&rdquo;; wl(s); wl(kp); s = &ldquo;25% lower bound.&rdquo; wl(s); wl(kbr); partName = problemNumber + &quot; (c.i)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 50, buttonLabel, 50, kNoShowAnswer); wl(kp); s = &ldquo;75% upper bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (c.ii)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 50, buttonLabel, 50, kNoShowAnswer); wl(kp); s = &ldquo;(d) The number of French speakers world-wide.&rdquo;; wl(s); wl(kp); s = &ldquo;25% lower bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (d.i)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;75% upper bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (d.ii)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;(e) The number of abortions performed in the U.S. each year.&rdquo;; wl(s); wl(kp); s = &ldquo;25% lower bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (e.i)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); s = &ldquo;75% upper bound.&rdquo;; wl(s); wl(kbr); partName = problemNumber + &quot; (e.ii)&rdquo;; problemName = &ldquo;prob&rdquo; + partName; buttonLabel = &ldquo;Check problem &quot; + partName; writeNumericBox(partName+&ldquo;id&rdquo;, 500000000, buttonLabel, 500000000, kNoShowAnswer); wl(kp); whr(kdivcol,kdivwid);
